from src.core.ControllerDiscovery import ControllerDiscoveryfrom src.core.Module import Modulefrom src.core.Router import Routerclass Core:    def __init__(self, app, router = Router(), controller_discovery= ControllerDiscovery()):        self.__app = app        self.__router = router        self.__controller_discovery = controller_discovery    def initModule(self, module : Module):        controllers = self.__controller_discovery.load(module)        self.__router.registry(self.__app, module.getRoutes(), controllers)