import osfrom src.core.Module import Modulefrom src.statistics.adapter import ormfrom src.statistics.config.config import get_upload_dir_pathfrom src.statistics.config.routes import get_routesfrom src.statistics.config.dependencies import dependenciesclass StatisticsModule(Module):    def __init__(self):        self.__startOrm()        self.__createUploadDir()    def getRoutes(self):        return get_routes()    def getControllersPath(self):        return os.path.join(os.path.dirname(__file__), 'controllers')    def getDependencies(self):        return dependencies    def __startOrm(self):        try:            orm.start_mappers()        except:            pass    def __createUploadDir(self):        try:            os.mkdir(get_upload_dir_path(), 0o755)        except OSError:            print("Creation of the directory %s failed" % get_upload_dir_path())        else:            print("Successfully created the directory %s" % get_upload_dir_path())