import mimetypesfrom flask import requestfrom functools import wrapsfrom src.statistics.views import StatisticViewclass StatisticsValidator:    @classmethod    def update_body_params(cls, fun):        @wraps(fun)        def wrapper(*args):            if request.values.get('name') is None :                return StatisticView().bad_request("Name missing") ,400            if request.values.get('separator') is None:                return StatisticView().bad_request("CSV separator missing"), 400            if request.files.get('file') is None:                return StatisticView().bad_request("File missing"), 400            file = request.files['file']            if  mimetypes.guess_type(file.filename)[0].__str__() != "text/csv":                return StatisticView().bad_request("Bad file type. Only text/csv is allowed"), 400            return fun(*args)        return wrapper