from src.core.model.Model import Modelclass Statistics(Model):    MAX_ROW = 1000    MAX_COLUMN = 20    def __init__(self,file :object, name: str,description: str,data_description : str, id :  int = None):        self.file =file        self.name =name        self.description = description        self.data_description = data_description        self.id = id    def rules(self):        nameErrMsg = lambda name : "Nazwa " + name + " ma nieprawidlowa dlugosc - minimum to 3 znaki, maksymalnie 50"        return [            ['name', lambda x : x is not None and len(x) <=50 and len(x) >= 3, nameErrMsg("statystyki")],            ['description', lambda x : x is None or  len(x) <=2048, "Opis statystyki ma nieprawidlowa dlugosc" ],            ['file.row', lambda x : x <= self.MAX_ROW and x >= 1, "Plik musi posiadac maksymalnie 1000 wierszy i minimum 1"],            ['file.column', lambda x : x <= self.MAX_COLUMN and x >= 1, "Plik musi posiadac maksymalnie 20 kolumn i minimum 1"],            ['file.name', lambda x : x is not None and len(x) <= 50 and len(x) >= 3, nameErrMsg("pliku")],        ]