from sqlalchemy import descfrom src.statistics.models.Statistics import Statisticsfrom src.core.repository.repository import SqlAlchemyRepositoryclass StatisticsRepository(SqlAlchemyRepository):    def _add(self, stats):        self.session.add(stats)    def _get(self, id):        return self.session.query(Statistics).filter_by(id=id).first()    def _getAll(self):        return self.session.query(Statistics).order_by(desc(Statistics.id)).all()    def _delete(self, id):        return self.session.query(Statistics).filter_by(id=id).delete()