from flask import jsonifyclass StatisticView:    def show(self,statistic):        return {            "id" : statistic.id,            "name" : statistic.name,            "description" : statistic.description,            "data_description" : statistic.data_description,            "file" : {                "name" :  statistic.file.name,                "row" : statistic.file.row,                "column"  : statistic.file.column,                "checksum"  : statistic.file.checksum            }        }    def show_all(self,statistics):        return jsonify([self.show(statistic) for statistic in statistics])    def not_found(self):        return {"status" : "error", "message" : "Statistics Not Found"}    def bad_request(self,message):        return {"status" : "error", "message" : message}